function start_count(){$(".count").each(function(){$(this).prop("Counter",0).animate({Counter:$(this).text()},{duration:3e3,easing:"swing",step:function(t){$(this).text(Math.ceil(t))}})})}$(function(){var t=$("#counter").offset().top-window.innerHeight;$(window).scroll(function(){var n=$("body").scrollTop();console.log(n+" - "+t),n>t&&start_count()})});